import React from 'react';

const shopItems = [
  { id: 'springShoes', name: 'Spring Shoes', cost: 15, hopsPerJumpBoost: 0.1 },
  { id: 'bouncyCarrot', name: 'Bouncy Carrot', cost: 100, hopsPerJumpBoost: 0.5 },
  // Add more items here
];

const HopShop = ({ totalHops, buyItem, ownedItems }) => (
  <div className="hop-shop">
    <h2>Hop Shop</h2>
    {shopItems.map((item) => (
      <div key={item.id} className="shop-item">
        <span>{item.name}</span>
        <span>Cost: {item.cost} Hops</span>
        <span>Owned: {ownedItems[item.id] || 0}</span>
        <button 
          onClick={() => buyItem(item)} 
          disabled={totalHops < item.cost}
        >
          Buy
        </button>
      </div>
    ))}
  </div>
);

export default HopShop;
