import React from 'react';

const itemEmojis = {
  springShoes: 'ðŸ‘Ÿ', bouncyCarrot: 'ðŸ¥•', /* ... add more emojis for each item ... */
};

const ActiveItems = ({ items }) => {
  return (
    <div className="active-items">
      <h2>Active Items</h2>
      <div className="items-grid">
        {Object.entries(items).map(([itemId, count]) => (
          <div key={itemId} className="item-slot">
            {count > 0 && (
              <>
                <span className="item-emoji">{itemEmojis[itemId]}</span>
                <span className="item-count">x{count}</span>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ActiveItems;
