import React, { useState, useEffect } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { saveUserProgress, loadUserProgress, fetchLeaderboardData } from '../utils/api';
import ActiveItems from './ActiveItems';
import HopShop from './HopShop';
import './HopperGame.css';

import skyImage from './images/sky.png';
import buildingsImage from './images/buildings.png';
import moonImage from './images/moon.png';
import rabbitImage from './images/rabbit.png';

const HopperGame = () => {
  const { publicKey } = useWallet();
  const [totalHops, setTotalHops] = useState(0);
  const [hopsPerJump, setHopsPerJump] = useState(1);
  const [activeItems, setActiveItems] = useState({});
  const [leaderboardData, setLeaderboardData] = useState([]);
  const walletAddress = publicKey ? publicKey.toBase58() : null;

  useEffect(() => {
    if (walletAddress) {
      loadUserProgress(walletAddress).then(progress => {
        if (progress) {
          setTotalHops(progress.totalHops || 0);
          setHopsPerJump(progress.hopsPerJump || 1);
          setActiveItems(progress.activeItems || {});
        }
      }).catch(console.error);
    } else {
      setTotalHops(0);
      setHopsPerJump(1);
      setActiveItems({});
    }
  }, [walletAddress]);

  useEffect(() => {
    fetchLeaderboardData().then(setLeaderboardData).catch(console.error);
  }, []);

  const handleJump = () => {
    const newTotalHops = totalHops + hopsPerJump;
    setTotalHops(newTotalHops);
    if (walletAddress) {
      saveUserProgress(walletAddress, {
        totalHops: newTotalHops,
        hopsPerJump,
        activeItems,
      }).catch(console.error);
    }
  };

  const buyItem = (item) => {
    if (totalHops >= item.cost && (activeItems[item.id] || 0) < 10) {
      setTotalHops(totalHops - item.cost);
      setHopsPerJump(hopsPerJump + item.hopsPerJumpBoost);
      setActiveItems({
        ...activeItems,
        [item.id]: (activeItems[item.id] || 0) + 1
      });
    }
  };

  return (
    <div className="hopper-game">
      <div className="game-section left">
        <div className="game-area" style={{backgroundImage: `url(${skyImage})`}}>
          <img src={moonImage} alt="Moon" className="moon" />
          <img src={rabbitImage} alt="Hopper" className="hopper" onClick={handleJump} />
          <div className="buildings" style={{backgroundImage: `url(${buildingsImage})`}}></div>
        </div>
        <div className="stats">
          <p>Total Hops: {totalHops}</p>
          <p>Hops per Jump: {hopsPerJump}</p>
        </div>
      </div>
      <div className="game-section middle">
        <ActiveItems items={activeItems} />
        <HopShop 
          totalHops={totalHops}
          buyItem={buyItem}
          activeItems={activeItems}
        />
      </div>
      <div className="game-section right">
        <WalletMultiButton />
        <div className="leaderboard">
          <h2>Top Hoppers</h2>
          <ul>
            {leaderboardData.map((entry, index) => (
              <li key={index}>
                {entry.walletAddress.slice(0, 4)}...{entry.walletAddress.slice(-4)}: {entry.totalHops} Hops
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HopperGame;
