import React, { useState, useEffect } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { saveUserProgress, loadUserProgress, fetchLeaderboardData } from '../utils/api';
import './HopperGame.css';

const HopperGame = () => {
  const { publicKey } = useWallet();
  const [totalHops, setTotalHops] = useState(0);
  const [pointsPerJump, setPointsPerJump] = useState(1);
  const [leaderboardData, setLeaderboardData] = useState([]);
  const walletAddress = publicKey ? publicKey.toBase58() : null;

  useEffect(() => {
    if (walletAddress) {
      loadUserProgress(walletAddress)
        .then(progress => {
          if (progress) {
            setTotalHops(progress.totalHops);
            setPointsPerJump(progress.pointsPerJump);
          }
        })
        .catch(console.error);
    }
  }, [walletAddress]);

  useEffect(() => {
    fetchLeaderboardData()
      .then(setLeaderboardData)
      .catch(console.error);
  }, []);

  const handleJump = () => {
    const newTotalHops = totalHops + pointsPerJump;
    setTotalHops(newTotalHops);
    if (walletAddress) {
      saveUserProgress(walletAddress, { totalHops: newTotalHops, pointsPerJump })
        .catch(console.error);
    }
    const rabbit = document.querySelector('.rabbit');
    rabbit.classList.add('hop');
    setTimeout(() => rabbit.classList.remove('hop'), 500);
  };

  const testBackendConnection = async () => {
    try {
      const response = await fetchLeaderboardData();
      console.log('Backend connection successful:', response);
      alert('Backend connection successful! Check console for details.');
    } catch (error) {
      console.error('Backend connection failed:', error);
      alert('Backend connection failed. Check console for details.');
    }
  };

  const shortenAddress = (address) => {
    return `${address.slice(0, 4)}...${address.slice(-4)}`;
  };

  return (
    <div className="game-container">
      <h1>Hop into the Future!</h1>
      <div className="game-area">
        <div className="sky">
          <div className="moon"></div>
          <div className="cloud"></div>
          <div className="cloud"></div>
          <div className="cloud"></div>
        </div>
        <div className="buildings"></div>
        <div className="rabbit" onClick={handleJump}></div>
      </div>
      <div className="game-info">
        <WalletMultiButton />
        {walletAddress && (
          <div>
            <p>Total Hops: {totalHops}</p>
            <p>Hops per Jump: {pointsPerJump}</p>
          </div>
        )}
        <button onClick={testBackendConnection}>Test Backend Connection</button>
      </div>
      <div className="leaderboard">
        <h2>Top 10 Hoppers</h2>
        <ul>
          {leaderboardData.map((entry, index) => (
            <li key={index} className="leaderboard-entry">
              <span className="rank">{index + 1}</span>
              <span className="wallet-address">{shortenAddress(entry.walletAddress)}</span>
              <span className="hop-count">{entry.totalHops} hops</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HopperGame;
