import React, { useState, useEffect } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { saveUserProgress, loadUserProgress, fetchLeaderboardData } from '../utils/api';
import './HopperGame.css';

const HopperGame = () => {
  const { publicKey } = useWallet();
  const [totalHops, setTotalHops] = useState(0);
  const [pointsPerJump, setPointsPerJump] = useState(1);
  const [leaderboardData, setLeaderboardData] = useState([]);
  const walletAddress = publicKey ? publicKey.toBase58() : null;

  // ... keep existing useEffect hooks and other functions ...

  const shortenAddress = (address) => {
    return `${address.slice(0, 4)}...${address.slice(-4)}`;
  };

  return (
    <div className="game-container">
      <div className="game-area">
        <h1>Hop into the Future!</h1>
        <div className="sky">
          <div className="moon"></div>
          <div className="cloud"></div>
          <div className="cloud"></div>
          <div className="cloud"></div>
        </div>
        <div className="buildings"></div>
        <div className="hop-count">Total Hops: {totalHops}</div>
        <div className="rabbit" onClick={handleJump}></div>
      </div>
      <div className="sidebar">
        <div className="wallet-section">
          <WalletMultiButton />
          {walletAddress && (
            <div>
              <p>Hops per Jump: {pointsPerJump}</p>
            </div>
          )}
        </div>
        <div className="leaderboard">
          <h2>Top 10 Hoppers</h2>
          <ul>
            {leaderboardData.map((entry, index) => (
              <li key={index} className="leaderboard-entry">
                <span className="rank">{index + 1}</span>
                <span className="wallet-address">{shortenAddress(entry.walletAddress)}</span>
                <span className="hop-count">{entry.totalHops} hops</span>
              </li>
            ))}
          </ul>
        </div>
        <button className="test-button" onClick={testBackendConnection}>Test Backend Connection</button>
      </div>
    </div>
  );
};

export default HopperGame;
