import axios from 'axios';

const API_URL = 'http://localhost:3001/api'; // We'll set up the backend later

export const saveUserProgress = async (walletAddress, data) => {
  try {
    const response = await axios.post(`${API_URL}/save-progress`, { walletAddress, data });
    return response.data;
  } catch (error) {
    console.error('Error saving progress:', error);
    throw error;
  }
};

export const loadUserProgress = async (walletAddress) => {
  try {
    const response = await axios.get(`${API_URL}/load-progress/${walletAddress}`);
    return response.data;
  } catch (error) {
    console.error('Error loading progress:', error);
    throw error;
  }
};

export const fetchLeaderboardData = async () => {
  try {
    const response = await axios.get(`${API_URL}/leaderboard`);
    return response.data;
  } catch (error) {
    console.error('Error fetching leaderboard:', error);
    throw error;
  }
};
